mapper.select:
SELECT BI.BORD_CODE AS 'BORDCODE', PD.COMPANYNAME, BI.MODEL_CODE AS 'MODELCODE', PD.PDNAME, FORMAT(BI.BORD_AMT, 0) AS 'BORDAMT', FORMAT((BI.BORD_AMT * PD.PD_PRICE), 0) AS 'TOTAL', DATE_FORMAT(DI.DIR_DATE, '%Y-%m-%d') AS 'DIRDATE', BI.BORD_TYPE AS 'BORDTYPE', DC.detail_name AS 'TYPENAME' FROM BORDER_INFO BI INNER JOIN DIRECTION DI ON BI.DIR_CODE = DI.DIR_CODE INNER JOIN (SELECT PD.MODEL_CODE, PD.PD_NAME AS 'PDNAME', PD.PD_PRICE, PD.loginID AS 'LOGINID', UI.company AS 'COMPANYNAME' FROM PD_INFO PD INNER JOIN tb_userinfo UI ON PD.loginID = UI.loginID) PD ON BI.MODEL_CODE = PD.MODEL_CODE INNER JOIN tb_detail_code DC ON DC.group_code = 'purchaseCD' AND BI.BORD_TYPE = DC.detail_code
AND BI.BORD_TYPE < 3
AND PD.PDNAME LIKE CONCAT('%', #{keyword}, '%')
AND DI.DIR_DATE BETWEEN #{startDate} AND #{endDate}
LIMIT #{startPage}, #{endPage}

mapper.update:
UPDATE	BORDER_INFO
SET		BORD_TYPE = #{bordType}
WHERE	BORD_CODE = #{bordCode}
