mapper.select:
SELECT 	@ROWNUM:=@ROWNUM + 1 AS 'NUM',
		OS.*
FROM 	ORDERSTATUS OS 
WHERE LOGINID = #{loginId} AND (@ROWNUM:=0) = 0

	AND JORDDATE BETWEEN #{startDate} AND #{endDate}

LIMIT #{startPage}, #{endPage}

SELECT 	COUNT(*)
FROM 	ORDERSTATUS
WHERE	LOGINID = #{loginId}

SELECT 	COUNT(*)
FROM 	ORDERSTATUSDETAIL
WHERE	JORDNO = #{jordNo}
