mapper.select:
SELECT NO.NOT_CODE AS 'NOTCODE', NO.NOT_TITLE AS 'NOTTITLE', NO.NOT_CON AS 'NOTCON', DATE_FORMAT(NO.NOT_DATE, '%Y-%m-%d') AS 'NOTDATE', NO.NOT_VIEW AS 'NOTVIEW', NO.loginID AS 'LOGINID', UI.name AS 'NAME' FROM NOTICE NO INNER JOIN tb_userinfo UI ON NO.loginID = UI.loginID
AND NO.NOT_TITLE LIKE CONCAT("%", #{keyword}, "%")
AND UI.name LIKE CONCAT("%", #{keyword}, "%")
AND NO.NOT_TITLE LIKE CONCAT("%", #{keyword}, "%") AND NO.NOT_CON LIKE CONCAT("%", #{keyword}, "%")
AND DATE_FORMAT(NO.NOT_DATE, '%Y%m%d') BETWEEN #{startDate} AND #{endDate}
AND NO.NOT_CODE = #{notCode}
ORDER BY NO.NOT_DATE DESC
LIMIT #{startPage}, #{endPage}

SELECT COUNT(*) FROM NOTICE NO INNER JOIN tb_userinfo UI ON NO.loginID = UI.loginID
AND NO.NOT_TITLE LIKE CONCAT("%", #{keyword}, "%")
AND UI.name LIKE CONCAT("%", #{keyword}, "%")
AND NO.NOT_TITLE LIKE CONCAT("%", #{keyword}, "%") AND NO.NOT_CON LIKE CONCAT("%", #{keyword}, "%")
AND DATE_FORMAT(NO.NOT_DATE, '%Y%m%d') BETWEEN #{startDate} AND #{endDate}
AND NO.NOT_CODE = #{notCode}
