mapper.select:
select pn.MODEL_NAME , rn.RE_DATE , rn.RE_AMT , pn.PD_PRICE*rn.RE_AMT as RE_PRICE , wn.WH_NAME , rn.re_code ,jn.loginID from RETURN_INFO rn inner join JORDER_INFO jn on rn.JORD_CODE = jn.JORD_CODE inner join PD_INFO pn on jn.MODEL_CODE = pn.MODEL_CODE inner join WHOUSE_INFO wn on rn.WH_CODE = wn.WH_CODE
and pn.MODEL_NAME like concat('%',#{pdname},'%')
and rn.RE_DATE >= #{sdate}
and rn.RE_DATE <= #{edate}
LIMIT #{pageIndex},#{pageSize}

select count(*) from RETURN_INFO rn inner join JORDER_INFO jn on rn.JORD_CODE = jn.JORD_CODE inner join PD_INFO pn on jn.MODEL_CODE = pn.MODEL_CODE inner join WHOUSE_INFO wn on rn.WH_CODE = wn.WH_CODE
and pn.MODEL_NAME like concat('%',#{pdname},'%')
and rn.RE_DATE >= #{sdate}
and rn.RE_DATE <= #{edate}
